mapper.select:
select
  id, lecturer_name, lecturer_user_no, lecturer_mobile, position, lecturer_email, introduce, head_img_url, audit_opinion, sort, lecturer_proportion, audit_status, status_id, gmt_create, gmt_modified
from education_user.lecturer_audit


        and id = #{id}


        and lecturer_name like CONCAT ('%',#{lecturerName},'%')


        and lecturer_user_no = #{lecturerUserNo}


        and lecturer_mobile = #{lecturerMobile}


        and position = #{position}


        and lecturer_email = #{lecturerEmail}


        and introduce = #{introduce}


        and head_img_url = #{headImgUrl}


        and audit_opinion = #{auditOpinion}


        and sort = #{sort}


        and lecturer_proportion = #{lecturerProportion}


        and audit_status = #{auditStatus}


        and status_id = #{statusId}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}
